/* TaxAid Africa - Main Stylesheet */
@import url('https://fonts.googleapis.com/css2?family=Architects+Daughter&family=DM+Sans:ital,opsz,wght@0,9..40,100..1000;1,9..40,100..1000&display=swap');

/* CSS Variables - Brand Color Palette */
:root {
  --brand-green: #208D71;
  --brand-red: #E64249;
  --brand-yellow: #F2BC1C;
  --brand-blue: #2D8CD5;
  --text-light: #FFFFFF;
  --text-dark: #333333;
  --text-grey: #555555;
  --overlay-color: rgba(16, 54, 45, 0.85);
  --taxaid-teal: #208D71;
  --taxaid-yellow: #F2BC1C;
  --taxaid-pink: #E64249;
  --taxaid-light: #f4f7f6;
  --foreground: #333333;
  --muted: #555555;
  --border: #e2e8f0;
  --card-bg: #ffffff;
  --radius: 0.75rem;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'DM Sans', sans-serif;
  background-color: var(--taxaid-light);
  color: var(--foreground);
  line-height: 1.6;
  -webkit-font-smoothing: antialiased;
}

.font-hand {
  font-family: 'Architects Daughter', cursive;
}

.container {
  max-width: 1280px;
  margin: 0 auto;
  padding: 0 1rem;
}

/* Buttons */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  border-radius: 9999px;
  font-weight: 700;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.2s ease;
  border: none;
  text-decoration: none;
}

.btn-primary {
  background-color: var(--taxaid-teal);
  color: white;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.btn-primary:hover {
  background-color: #004d45;
  transform: translateY(-2px);
  box-shadow: 0 6px 10px -2px rgba(0, 0, 0, 0.15);
}

.btn-secondary {
  background-color: var(--taxaid-yellow);
  color: #000;
  box-shadow: 0 4px 0 0 rgba(0, 0, 0, 0.2);
}

.btn-secondary:hover {
  background-color: #eaab1f;
  transform: translateY(2px);
  box-shadow: 0 2px 0 0 rgba(0, 0, 0, 0.2);
}

.btn-accent {
  background-color: var(--taxaid-pink);
  color: white;
  box-shadow: 0 4px 0 0 rgba(0, 0, 0, 0.2);
}

.btn-accent:hover {
  background-color: #e05a70;
  transform: translateY(2px);
  box-shadow: 0 2px 0 0 rgba(0, 0, 0, 0.2);
}

.btn-outline {
  background-color: transparent;
  border: 2px solid var(--taxaid-teal);
  color: var(--taxaid-teal);
}

.btn-outline:hover {
  background-color: var(--taxaid-teal);
  color: white;
}

.btn-ghost {
  background-color: transparent;
  color: var(--foreground);
}

.btn-ghost:hover {
  background-color: rgba(0, 0, 0, 0.05);
}

.btn-lg {
  padding: 1rem 2rem;
  font-size: 1.25rem;
  height: 4rem;
}

.btn-sm {
  padding: 0.5rem 1rem;
  font-size: 0.875rem;
}

/* Cards */
.card {
  background: var(--card-bg);
  border-radius: var(--radius);
  border: 1px solid var(--border);
  overflow: hidden;
}

/* Form Elements */
.input, .textarea, .select {
  width: 100%;
  padding: 0.75rem 1rem;
  border: 1px solid var(--border);
  border-radius: var(--radius);
  font-size: 1rem;
  font-family: inherit;
  background-color: #f8fafc;
  transition: all 0.2s ease;
}

.input:focus, .textarea:focus, .select:focus {
  outline: none;
  border-color: var(--taxaid-teal);
  background-color: white;
  box-shadow: 0 0 0 3px rgba(0, 95, 86, 0.1);
}

.input-lg {
  padding: 1rem 1.25rem;
  font-size: 1.125rem;
  height: 4rem;
  border-radius: 1rem;
}

.label {
  display: block;
  font-weight: 700;
  margin-bottom: 0.5rem;
  color: rgba(0, 0, 0, 0.7);
}

/* Grid Pattern Background */
.bg-grid-pattern {
  background-size: 60px 60px;
  background-image:
    linear-gradient(to right, rgba(255, 255, 255, 0.08) 1px, transparent 1px),
    linear-gradient(to bottom, rgba(255, 255, 255, 0.08) 1px, transparent 1px);
}

/* Animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes float {
  0%, 100% { transform: translateY(0) rotate(0deg); }
  50% { transform: translateY(-10px) rotate(-5deg); }
}

@keyframes pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.5; }
}

@keyframes spin-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.animate-fade-in-up {
  animation: fadeInUp 0.6s ease forwards;
}

.animate-float {
  animation: float 4s ease-in-out infinite;
}

.animate-pulse {
  animation: pulse 2s ease-in-out infinite;
}

.animate-spin-slow {
  animation: spin-slow 10s linear infinite;
}

/* Utility Classes */
.text-primary { color: var(--taxaid-teal); }
.text-secondary { color: var(--taxaid-yellow); }
.text-accent { color: var(--taxaid-pink); }
.text-muted { color: var(--muted); }
.text-white { color: white; }

.bg-primary { background-color: var(--taxaid-teal); }
.bg-secondary { background-color: var(--taxaid-yellow); }
.bg-accent { background-color: var(--taxaid-pink); }
.bg-light { background-color: var(--taxaid-light); }
.bg-white { background-color: white; }

.rounded-full { border-radius: 9999px; }
.rounded-2xl { border-radius: 1rem; }
.rounded-3xl { border-radius: 1.5rem; }

.shadow-lg { box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05); }
.shadow-xl { box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04); }
.shadow-2xl { box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25); }

/* Responsive Grid */
.grid { display: grid; gap: 1rem; }
.grid-cols-2 { grid-template-columns: repeat(2, 1fr); }
.grid-cols-3 { grid-template-columns: repeat(3, 1fr); }
.grid-cols-12 { grid-template-columns: repeat(12, 1fr); }

.col-span-2 { grid-column: span 2; }
.col-span-3 { grid-column: span 3; }
.col-span-5 { grid-column: span 5; }

.flex { display: flex; }
.flex-col { flex-direction: column; }
.flex-wrap { flex-wrap: wrap; }
.items-center { align-items: center; }
.items-start { align-items: flex-start; }
.justify-center { justify-content: center; }
.justify-between { justify-content: space-between; }
.gap-1 { gap: 0.25rem; }
.gap-2 { gap: 0.5rem; }
.gap-3 { gap: 0.75rem; }
.gap-4 { gap: 1rem; }
.gap-6 { gap: 1.5rem; }
.gap-8 { gap: 2rem; }
.gap-12 { gap: 3rem; }
.gap-16 { gap: 4rem; }

/* Spacing */
.p-2 { padding: 0.5rem; }
.p-4 { padding: 1rem; }
.p-6 { padding: 1.5rem; }
.p-8 { padding: 2rem; }
.p-12 { padding: 3rem; }
.px-4 { padding-left: 1rem; padding-right: 1rem; }
.px-8 { padding-left: 2rem; padding-right: 2rem; }
.py-2 { padding-top: 0.5rem; padding-bottom: 0.5rem; }
.py-4 { padding-top: 1rem; padding-bottom: 1rem; }
.py-12 { padding-top: 3rem; padding-bottom: 3rem; }
.py-20 { padding-top: 5rem; padding-bottom: 5rem; }
.py-24 { padding-top: 6rem; padding-bottom: 6rem; }
.pt-8 { padding-top: 2rem; }
.pt-20 { padding-top: 5rem; }
.pb-8 { padding-bottom: 2rem; }
.pb-10 { padding-bottom: 2.5rem; }
.mb-2 { margin-bottom: 0.5rem; }
.mb-4 { margin-bottom: 1rem; }
.mb-6 { margin-bottom: 1.5rem; }
.mb-8 { margin-bottom: 2rem; }
.mb-10 { margin-bottom: 2.5rem; }
.mb-12 { margin-bottom: 3rem; }
.mb-16 { margin-bottom: 4rem; }
.mt-4 { margin-top: 1rem; }
.mt-6 { margin-top: 1.5rem; }
.mt-8 { margin-top: 2rem; }
.mt-12 { margin-top: 3rem; }
.ml-2 { margin-left: 0.5rem; }

/* Typography */
.text-xs { font-size: 0.75rem; }
.text-sm { font-size: 0.875rem; }
.text-base { font-size: 1rem; }
.text-lg { font-size: 1.125rem; }
.text-xl { font-size: 1.25rem; }
.text-2xl { font-size: 1.5rem; }
.text-3xl { font-size: 1.875rem; }
.text-4xl { font-size: 2.25rem; }
.text-5xl { font-size: 3rem; }
.text-6xl { font-size: 3.75rem; }
.text-7xl { font-size: 4.5rem; }

.font-bold { font-weight: 700; }
.font-light { font-weight: 300; }
.font-medium { font-weight: 500; }
.italic { font-style: italic; }
.uppercase { text-transform: uppercase; }
.tracking-wider { letter-spacing: 0.05em; }
.tracking-widest { letter-spacing: 0.1em; }
.leading-none { line-height: 1; }
.leading-tight { line-height: 1.25; }
.leading-relaxed { line-height: 1.625; }

.text-center { text-align: center; }
.underline { text-decoration: underline; }
.underline-offset-4 { text-underline-offset: 4px; }
.decoration-2 { text-decoration-thickness: 2px; }

/* Layout */
.relative { position: relative; }
.absolute { position: absolute; }
.sticky { position: sticky; }
.fixed { position: fixed; }
.top-0 { top: 0; }
.left-0 { left: 0; }
.right-0 { right: 0; }
.bottom-0 { bottom: 0; }
.inset-0 { inset: 0; }
.z-10 { z-index: 10; }
.z-20 { z-index: 20; }
.z-50 { z-index: 50; }

.w-full { width: 100%; }
.w-fit { width: fit-content; }
.max-w-md { max-width: 28rem; }
.max-w-lg { max-width: 32rem; }
.max-w-xl { max-width: 36rem; }
.max-w-2xl { max-width: 42rem; }
.max-w-3xl { max-width: 48rem; }
.max-w-5xl { max-width: 64rem; }

.min-h-screen { min-height: 100vh; }
.h-screen { height: 100vh; }
.h-full { height: 100%; }

.overflow-hidden { overflow: hidden; }
.overflow-x-auto { overflow-x: auto; }

.opacity-20 { opacity: 0.2; }
.opacity-40 { opacity: 0.4; }
.opacity-50 { opacity: 0.5; }
.opacity-70 { opacity: 0.7; }
.opacity-80 { opacity: 0.8; }
.opacity-90 { opacity: 0.9; }

.pointer-events-none { pointer-events: none; }
.cursor-pointer { cursor: pointer; }

.border { border: 1px solid var(--border); }
.border-t { border-top: 1px solid var(--border); }
.border-b { border-bottom: 1px solid var(--border); }
.border-transparent { border-color: transparent; }
.border-2 { border-width: 2px; }
.border-4 { border-width: 4px; }

/* Transforms */
.transform { transform: translateZ(0); }
.rotate-1 { transform: rotate(1deg); }
.rotate-2 { transform: rotate(2deg); }
.-rotate-2 { transform: rotate(-2deg); }
.scale-110 { transform: scale(1.1); }
.origin-left { transform-origin: left; }

.transition-all { transition: all 0.3s ease; }
.transition-transform { transition: transform 0.3s ease; }
.transition-colors { transition: color 0.2s ease, background-color 0.2s ease; }
.duration-300 { transition-duration: 300ms; }
.duration-500 { transition-duration: 500ms; }
.duration-700 { transition-duration: 700ms; }

/* Visibility */
.hidden { display: none; }
.block { display: block; }
.inline-block { display: inline-block; }
.inline-flex { display: inline-flex; }

/* Scrolling */
.scroll-mt-20 { scroll-margin-top: 5rem; }

/* Object Fit */
.object-cover { object-fit: cover; }
.object-top { object-position: top; }

/* SVG Doodles */
.doodle {
  fill: none;
  stroke: currentColor;
  stroke-width: 3;
  stroke-linecap: round;
  stroke-linejoin: round;
}

/* Custom Responsive Grid Classes */
.hero-grid,
.about-grid,
.support-grid,
.calculator-grid {
  grid-template-columns: 1fr;
}

.services-grid,
.testimonials-grid {
  grid-template-columns: 1fr;
}

.support-services-grid,
.form-name-grid,
.tabs-grid {
  grid-template-columns: 1fr;
}

.footer-grid {
  grid-template-columns: 1fr;
}

.footer-brand,
.footer-links,
.footer-contact {
  width: 100%;
}

/* ===========================================
   RESPONSIVE BREAKPOINTS (Mobile-First)
   - Mobile: < 640px (default styles)
   - Tablet: >= 640px and < 1024px
   - Desktop: >= 1024px
   =========================================== */

/* ===== MOBILE STYLES (< 640px) ===== */
@media (max-width: 639px) {
  /* Typography scaling for mobile */
  .text-7xl { font-size: 2.5rem; }
  .text-6xl { font-size: 2rem; }
  .text-5xl { font-size: 1.75rem; }
  .text-4xl { font-size: 1.5rem; }
  .text-3xl { font-size: 1.25rem; }

  /* Spacing adjustments */
  .py-24 { padding-top: 2rem; padding-bottom: 2rem; }
  .py-20 { padding-top: 1.5rem; padding-bottom: 1.5rem; }
  .gap-16 { gap: 1.5rem; }
  .gap-12 { gap: 1rem; }
  .gap-8 { gap: 0.75rem; }

  /* Container padding */
  .container { padding: 0 1rem; }

  /* Header logo - smaller on mobile */
  header .h-10 { height: 2rem; }

  /* Hero section mobile */
  .hero-grid { gap: 1.5rem; }
  section.min-h-\[650px\] { min-height: auto; padding-bottom: 2rem; }

  /* Buttons smaller on mobile */
  .btn-lg {
    padding: 0.75rem 1.5rem;
    font-size: 1rem;
    height: auto;
  }

  /* Cards full width */
  .card { margin-left: 0; margin-right: 0; }

  /* Footer mobile */
  .footer-grid { gap: 2rem; }

  /* Form elements */
  .input-lg {
    padding: 0.75rem 1rem;
    font-size: 1rem;
    height: 3rem;
  }
}

/* ===== SMALL TABLET (>= 640px) ===== */
@media (min-width: 640px) {
  .sm\:flex { display: flex; }
  .sm\:hidden { display: none !important; }
  .sm\:block { display: block; }
  .sm\:flex-row { flex-direction: row; }
  .sm\:grid-cols-2 { grid-template-columns: repeat(2, 1fr); }
  .sm\:h-12 { height: 3rem; }
  .sm\:gap-4 { gap: 1rem; }
  .sm\:gap-6 { gap: 1.5rem; }
  .sm\:text-4xl { font-size: 2.25rem; }
  .sm\:text-5xl { font-size: 3rem; }

  /* Header logo - moderate on tablet */
  header .h-10 { height: 2.5rem; }

  /* 2-column grids on tablet */
  .support-services-grid,
  .form-name-grid,
  .tabs-grid {
    grid-template-columns: repeat(2, 1fr);
  }

  /* Container padding */
  .container { padding: 0 1.5rem; }
}

/* ===== TABLET (>= 768px) ===== */
@media (min-width: 768px) {
  .md\:hidden { display: none !important; }
  .md\:block { display: block; }
  .md\:flex { display: flex; }
  .md\:grid-cols-2 { grid-template-columns: repeat(2, 1fr); }
  .md\:grid-cols-3 { grid-template-columns: repeat(3, 1fr); }
  .md\:flex-row { flex-direction: row; }
  .md\:w-auto { width: auto; }
  .md\:py-20 { padding-top: 5rem; padding-bottom: 5rem; }
  .md\:text-6xl { font-size: 3.75rem; }
  .md\:text-5xl { font-size: 3rem; }
  .md\:text-4xl { font-size: 2.25rem; }
  .md\:basis-1\/2 { flex-basis: 50%; }
  .md\:gap-8 { gap: 2rem; }
  .md\:gap-12 { gap: 3rem; }

  /* Header logo - full size on tablet+ */
  header .h-10 { height: 2.75rem; }

  /* 2-column layouts on tablet */
  .hero-grid,
  .about-grid,
  .support-grid,
  .calculator-grid {
    grid-template-columns: repeat(2, 1fr);
  }

  .footer-grid {
    grid-template-columns: repeat(2, 1fr);
    gap: 2rem;
  }

  /* Typography */
  .text-6xl { font-size: 3rem; }
  .text-5xl { font-size: 2.5rem; }
  .text-4xl { font-size: 2rem; }

  /* Spacing */
  .py-24 { padding-top: 4rem; padding-bottom: 4rem; }
  .gap-16 { gap: 3rem; }

  /* Container */
  .container { padding: 0 2rem; }
}

/* ===== DESKTOP (>= 1024px) ===== */
@media (min-width: 1024px) {
  .lg\:flex { display: flex; }
  .lg\:hidden { display: none !important; }
  .lg\:block { display: block; }
  .lg\:basis-1\/3 { flex-basis: 33.333333%; }
  .lg\:gap-8 { gap: 2rem; }
  .lg\:gap-12 { gap: 3rem; }
  .lg\:gap-16 { gap: 4rem; }
  .lg\:text-6xl { font-size: 3.75rem; }
  .lg\:text-5xl { font-size: 3rem; }
  .lg\:py-24 { padding-top: 6rem; padding-bottom: 6rem; }

  /* Header logo - full size on desktop */
  header .h-10 { height: 3rem; }

  /* 3-column grids on desktop */
  .services-grid,
  .testimonials-grid {
    grid-template-columns: repeat(3, 1fr);
  }

  /* Footer 4-column on desktop */
  .footer-grid {
    grid-template-columns: 2fr 1fr 1fr 1.5fr;
    gap: 2.5rem;
  }

  /* Full typography sizes */
  .text-7xl { font-size: 4.5rem; }
  .text-6xl { font-size: 3.75rem; }
  .text-5xl { font-size: 3rem; }

  /* Full spacing */
  .py-24 { padding-top: 6rem; padding-bottom: 6rem; }
  .gap-16 { gap: 4rem; }
  .gap-12 { gap: 3rem; }

  /* Container */
  .container { padding: 0 2rem; max-width: 1280px; }

  /* Hero section */
  section.min-h-\[650px\] { min-height: 650px; }
}

/* ===== LARGE DESKTOP (>= 1280px) ===== */
@media (min-width: 1280px) {
  .xl\:flex { display: flex; }
  .xl\:block { display: block; }
  .xl\:hidden { display: none !important; }
  .xl\:gap-6 { gap: 1.5rem; }
  .xl\:gap-3 { gap: 0.75rem; }
  .xl\:gap-8 { gap: 2rem; }

  /* Header logo - slightly larger on big screens */
  header .h-10 { height: 3.25rem; }

  /* Container max-width */
  .container { max-width: 1400px; }
}

/* ===== EXTRA LARGE DESKTOP (>= 1536px) ===== */
@media (min-width: 1536px) {
  .container { max-width: 1536px; }

  /* Header logo - max size on very large screens */
  header .h-10 { height: 3.5rem; }
}

/* Hover Effects */
.hover\:scale-105:hover { transform: scale(1.05); }
.hover\:scale-110:hover { transform: scale(1.1); }
.hover\:-translate-y-1:hover { transform: translateY(-4px); }
.hover\:-translate-y-2:hover { transform: translateY(-8px); }
.hover\:shadow-lg:hover { box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1); }
.hover\:shadow-xl:hover { box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1); }
.hover\:border-primary:hover { border-color: var(--taxaid-teal); }
.hover\:border-secondary:hover { border-color: var(--taxaid-yellow); }
.hover\:bg-primary:hover { background-color: var(--taxaid-teal); }
.hover\:text-white:hover { color: white; }
.hover\:text-primary:hover { color: var(--taxaid-teal); }
.group:hover .group-hover\:scale-110 { transform: scale(1.1); }
.group:hover .group-hover\:opacity-100 { opacity: 1; }
.group:hover .group-hover\:bg-primary { background-color: var(--taxaid-teal); }
.group:hover .group-hover\:text-white { color: white; }

/* Shrink utility */
.shrink-0 { flex-shrink: 0; }
.flex-grow { flex-grow: 1; }

/* Header Logo Responsive Sizing */
.header-logo {
  height: 2.25rem;
  transition: height 0.2s ease;
}

@media (min-width: 640px) {
  .header-logo { height: 2.5rem; }
}

@media (min-width: 768px) {
  .header-logo { height: 2.75rem; }
}

@media (min-width: 1024px) {
  .header-logo { height: 3rem; }
}

@media (min-width: 1280px) {
  .header-logo { height: 3.25rem; }
}

/* No resize */
.resize-none { resize: none; }

